ARK.firstLoad = true;
ARK.pageHistory = [];
ARK.history = {
	phone: [],
	tablet: [],
	currentPage: '#splash',
	pageBeingBuilt: '',
	goingBack: false,
	pushCurrentPage: function (options) {
		var currentPageParams, pageBeingBuiltParams;

		if (this.goingBack) {
			this.goingBack = false;
			return;
		}

		options = options || {};

		// links in the nav shouldn't be added to the stack
		if (options.navLink) {
			return;
		}

		// If we're going to the first page of the app, then no history is needed.
		if (this.currentPage === '#splash') {
			this.phone = [];
			this.tablet = [];
			// Except...if this is a responsive device starting in tablet view, then
			// we need to push top-level onto the top of phone history
			if (ARK.isDeviceResponsive() && ARK.showingTabletView()) {
				this.phone.push({ parameters: ARK.phoneHomePage });
			}
			return;
		}

		if (ARK.isDevicePhoneOnly()) {
			this.phone.push({ parameters: this.currentPage });
		} else if (ARK.isDeviceTabletOnly()) {
			this.tablet.push({ parameters: this.currentPage });
		} else {
			pageBeingBuiltParams = ARK.parseHashVars(this.pageBeingBuilt);
			currentPageParams = ARK.parseHashVars(this.currentPage);

			// Always push the page onto the phone stack
			this.phone.push({ parameters: this.currentPage });

			// This is used by phone product popup nav right now. Therefore, it doesn't
			// need to check for any of the extra phone pushes below
			if (options.responsiveSkipTabletHistory) {
				this.phone[this.phone.length - 1].onlyPopPhone = true;
				return;
			}

			// If we're going to the specs page and the top of the phone stack isn't product, then add it
			if (pageBeingBuiltParams.page === 'specs' && ARK.parseHashVars(this.phone[this.phone.length - 1].parameters).page !== 'product') {
				this.phone.push({ parameters: 'page=product&id=' + pageBeingBuiltParams.id });
			}

			// If we're going to the compare page and the top of the phone stack isn't queue, then add it
			if (pageBeingBuiltParams.page === 'compare' && ARK.parseHashVars(this.phone[this.phone.length - 1].parameters).page !== 'queue') {
				this.phone.push({ parameters: 'page=queue' });
			}

			// If we're going to the compare page and the top of the tablet stack is empty, then add tablet home page
			if (pageBeingBuiltParams.page === 'compare' && this.tablet.length === 0) {
				this.tablet.push({ parameters: ARK.tabletHomePage });
			}

			// Only push tablet pages onto the tablet stack
			if (currentPageParams.page !== 'top-level' && currentPageParams.page !== 'product' && currentPageParams.page !== 'queue') {
				this.tablet.push({ parameters: this.currentPage });
			}
		}
	},
	setCurrentPage: function (page) {
		this.currentPage = page;
	},
	setPageBeingBuilt: function (page) {
		this.pageBeingBuilt = page;
	},
	goBack: function (nextWrapperDiv) {
		var self = this;

		return function () {
			var parameters, historyItem;

			self.goingBack = true;

			if (ARK.isDevicePhoneOnly()) {
				if (self.phone.length < 1) {
					parameters = ARK.phoneHomePage;
					ARK.logError('Empty phone-only stack on ' + self.currentPage);
				} else {
					parameters = self.phone.pop().parameters;
				}
			} else if (ARK.isDeviceTabletOnly()) {
				if (self.tablet.length < 1) {
					parameters = ARK.tabletHomePage;
					ARK.logError('Empty tablet-only stack on ' + self.currentPage);
				} else {
					parameters = self.tablet.pop().parameters;
				}
			} else if (ARK.showingTabletView()) {
				if (self.tablet.length < 1) {
					parameters = ARK.tabletHomePage;
					ARK.logError('Empty tablet stack on ' + self.currentPage);
				} else {
					parameters = self.tablet.pop().parameters;
					if (self.phone.length < 1) {
						ARK.logError('Empty phone stack (tablet view) on ' + self.currentPage);
					}
					// Get rid of extra pages added while navigating a product
					while (self.phone[self.phone.length - 1].onlyPopPhone) {
						self.phone.pop();
					}
					self.phone.pop();
					// Pop another page if the new page matches the top of the phone stack
					if (self.phone[self.phone.length - 1].parameters === parameters) {
						self.phone.pop();
					}
				}
			} else {
				if (self.phone.length < 1) {
					parameters = ARK.phoneHomePage;
					ARK.logError('Empty phone stack on ' + self.currentPage);
				} else {
					historyItem = self.phone.pop();
					parameters = historyItem.parameters;
					// Skip popping the tablet if told to
					if (historyItem.onlyPopPhone) {
					}
					else if (self.tablet.length === 0 || self.phone.length === 0) {
					}
					// If the new top of the phone stack doesn't match the current top of the tablet stack
					// then pop the tablet stack
					else if (self.phone[self.phone.length - 1].parameters !== self.tablet[self.tablet.length - 1].parameters) {
						self.tablet.pop();
					}
				}
			}
			return nextWrapperDiv + '?' + parameters;
		};
	},
	goHome: function (nextWrapperDiv) {
		var self = this;

		return function () {
			// Reset history
			self.phone = [];
			self.tablet = [];
			// If this is a responsive device going home in tablet view, then
			// we need to push top-level onto the top of phone history
			if (ARK.isDeviceResponsive() && ARK.showingTabletView()) {
				self.phone.push({ parameters: ARK.phoneHomePage });
			}

			self.goingBack = true;

			if (ARK.showingTabletView()) {
				return nextWrapperDiv + '?' + ARK.tabletHomePage;
			} else {
				return nextWrapperDiv + '?' + ARK.phoneHomePage;
			}
		};
	}
};
