ARK.loadQueue = function () {
	var queue = window.localStorage.getItem('queue');

	if (queue !== null) {
		try {
			ARK.queue = jQuery.parseJSON(queue);
		}
		catch (ex) { }
	}
	if (ARK.debug) { alert('queue loaded'); }
	ARK.initialLoadStatus.queueLoaded = true;
	ARK.initialize();
};

ARK.getDeviceSize = function () {
	var width = window.innerWidth,
		height = window.innerHeight,
		obj = {};

	if (height > width) {
		obj.width = width;
		obj.height = height;
	} else {
		obj.width = height;
		obj.height = width;
	}

	return obj;
};

ARK.setDeviceSize = function () {
	ARK.deviceSize = ARK.getDeviceSize();
};

ARK.setDeviceType = function () {
	ARK.deviceType = 'phone';
	// deviceSize doesn't change with orientation. So height is always the long side

	// Tablet will work in landscape, but not in portrait
	if (ARK.deviceSize.height >= 640 && ARK.deviceSize.width < 640) {
		ARK.deviceType = 'variable';
	} else if (ARK.deviceSize.height >= 640 && ARK.deviceSize.width >= 640) { // Can we display the tablet layout in both orientations?
		// Treat all iOS 9 tablets as variable to handle multi-screen views
		if (ARK.isiOS9()) {
			ARK.deviceType = 'variable';
		} else {
			ARK.deviceType = 'tablet';
		}
	}

	// The Kindle Fire won't scroll in the landscape layout. The issue
	// appears to be having multiple scrollable areas on the page. So... for the
	// Fire, use the phone layout
	if (ARK.isGingerbread() && device.model === 'Kindle Fire') {
		ARK.deviceType = 'phone';
	}

	if (ARK.isWindows8()) {
		ARK.deviceType = 'variable';
	}

};

ARK.setContentSize = function () {
	ARK.setDeviceSize();
	// Height should be full width minus height of header
	if (ARK.isPortrait()) {
		$('.content').css('width', ARK.deviceSize.width).css('height', ARK.deviceSize.height - 45);
	} else {
		$('.content').css('width', ARK.deviceSize.height).css('height', ARK.deviceSize.width - 45);
	}
};

ARK.setContentHeight = function() {
	ARK.setDeviceSize();
	// Height should be full width minus height of header
	if (ARK.isPortrait()) {
		$('.content').css('height', ARK.deviceSize.height - 45);
		$('.tablet .ui-block-a.tablet-nav, .search-popup .tablet-popup .nav, .search-popup .tablet-popup .search-content').css('height', ARK.deviceSize.height - 45);
	} else {
		$('.content').css('height', ARK.deviceSize.width - 45);
		$('.tablet .ui-block-a.tablet-nav, .search-popup .tablet-popup .nav, .search-popup .tablet-popup .search-content').css('height', ARK.deviceSize.width - 45);
	}
};

ARK.getDevicePixelRatio = function () {
	return window.devicePixelRatio || 1;
};

ARK.isDeviceTabletOnly = function () {
	return ARK.deviceType === 'tablet';
};

ARK.isDevicePhoneOnly = function () {
	return ARK.deviceType === 'phone';
};

ARK.isDeviceResponsive = function () {
	return ARK.deviceType === 'variable';
};

ARK.setDeviceOrientation = function () {
	ARK.deviceOrientation = window.innerWidth < window.innerHeight ? 'portrait' : 'landscape';
};

ARK.isPortrait = function () {
	return ARK.deviceOrientation === 'portrait';
};

ARK.isLandscape = function () {
	return ARK.deviceOrientation === 'landscape';
};

ARK.showingPhoneView = function () {
	return ARK.isDevicePhoneOnly() || ARK.isDeviceResponsive() && window.innerWidth < 640;
};

ARK.showingTabletView = function () {
	return ARK.isDeviceTabletOnly() || ARK.isDeviceResponsive() && window.innerWidth >= 640;
};

ARK.isiOS = function () {
	var platform = ARK.devicePlatform();

	return platform === 'iOS';
};

ARK.isiOS6 = function () {
	return ARK.isiOS() && ARK.deviceVersion().slice(0, 2) === '6.';
};

ARK.isiOS7 = function () {
	return ARK.isiOS() && ARK.deviceVersion().slice(0, 2) === '7.';
};

ARK.isiOS8 = function () {
	return ARK.isiOS() && ARK.deviceVersion().slice(0, 2) === '8.';
};

ARK.isiOS9 = function () {
	return ARK.isiOS() && ARK.deviceVersion().slice(0, 2) === '9.';
};

ARK.isAndroid = function () {
	return ARK.devicePlatform && ARK.devicePlatform() === 'Android';
};

ARK.isIceCreamSandwich = function () {
	return ARK.isAndroid() && ARK.deviceVersion().slice(0, 3) === '4.0';
};

ARK.isGingerbread = function () {
	return ARK.isAndroid() && ARK.deviceVersion().slice(0, 3) === '2.3';
};

ARK.isHoneycomb = function () {
	return ARK.isAndroid() && ARK.deviceVersion().slice(0, 2) === '3.';
};

ARK.isJellybean = function () {
	var version = ARK.deviceVersion().slice(0, 3);

	return ARK.isAndroid() && (version === '4.1' || version === '4.2');
};

ARK.isJellybean42 = function () {
	return ARK.isAndroid() && ARK.deviceVersion().slice(0, 3) === '4.2';
};

ARK.isJellybeanOrOlder = function () {
	return ARK.isAndroid() && ARK.deviceVersion().slice(0, 1) < 5;
};

ARK.isLollipop = function () {
	return ARK.isAndroid() && ARK.deviceVersion().slice(0, 3) === '5.0';
};

ARK.isWeb = function () {
	return ARK.devicePlatform && ARK.devicePlatform() === 'Web';
};

ARK.isWindows8 = function () {
	return ARK.devicePlatform && ARK.devicePlatform() === 'windows8';
};

ARK.isWindows8RT = function () {
	return ((typeof WinJS !== 'undefined') && (typeof WinJS.Application !== 'undefined'));
};

ARK.initialize = function () {
	var msgStrings, lang, msgIndex, originalLang;

	if (ARK.initialLoadStatus.ready()) {
		$('#message').html(ARK.translations.get('Checking'));
		if (ARK.settings.testSplash) {
			$('#screen-size').show();
			$('#height').text(window.innerHeight);
			$('#width').text(window.innerWidth);
			msgStrings = [];
			msgIndex = 0;
			originalLang = ARK.settings.language;
			for (lang in ARK.settings.languages) {
				ARK.settings.language = lang;
				msgStrings[msgIndex++] = { string: 'Product Specifications', lang: lang };
				msgStrings[msgIndex++] = { string: ARK.translations.get('Loading'), lang: lang };
				msgStrings[msgIndex++] = { string: ARK.translations.get('Checking'), lang: lang };
				msgStrings[msgIndex++] = { string: ARK.translations.get('Updating'), lang: lang };
			}
			msgIndex = 2;

			ARK.settings.language = originalLang;

			ARK.setClickEvent($('body'), function (e) {
				$('body').removeClass(msgStrings[msgIndex].lang);
				if (e.pageX < window.innerWidth / 2) {
					msgIndex--;
					if (msgIndex < 0) { msgIndex = msgStrings.length - 1; }
				} else {
					msgIndex++;
					if (msgIndex >= msgStrings.length) { msgIndex = 0; }
				}
				if (msgIndex % 4 === 3) {
					$('#progress-bar').show();
				} else {
					$('#progress-bar').hide();
				}
				$('body').addClass(msgStrings[msgIndex].lang);
				$('#message').html(msgStrings[msgIndex].string);
			});
		} else {
			ARK.startAppWithDatabaseUpdateCheck('#a', 'App startup');
		}
	}
};

