ARK.transitionJqt = function (from, to, animation, reverse) {
	var animationClasses = 'animating animating3d',
		animationName = animation;

	function reverseAnimation (animation) {
		var opposites = {
			'up' : 'down',
			'down' : 'up',
			'left' : 'right',
			'right' : 'left',
			'in' : 'out',
			'out' : 'in'
		};

		return opposites[animation] || animation;
	}

	function navigationEndHandler() {
		var bufferTime = 100;

		if (animationName !== 'none') {
			from.removeClass('ui-page-active ' + animationName + ' jqt-out');
			to.removeClass(animationName);
			$('body').removeClass(animationClasses);
		} else {
			from.removeClass(animationName + ' jqt-out ui-page-active');
			bufferTime += 260;
		}

		setTimeout(function () { to.removeClass('jqt-in'); }, bufferTime);

		return true;
	}

	if (animationName !== 'none') {
		if (reverse) {
			animationName = animationName.replace(/left|right|up|down|in|out/, reverseAnimation);
		}

		from.one('webkitAnimationEnd', navigationEndHandler);

		$('body').addClass(animationClasses);

		window.scrollTo(0, 0);

		to.addClass(animationName + ' jqt-in ui-page-active');
		from.addClass(animationName + ' jqt-out');
	} else {
		window.scrollTo(0, 0);
		to.addClass('jqt-in ui-page-active');
		navigationEndHandler();
	}
};

$.fn.animationComplete = function (callback) {
	return $(this).one('webkitAnimationEnd animationend', callback);
};

ARK.transitionJqm = function ($to, options) {
	var sequential = options.transition !== 'slide',
		reverseClass = options.reverse ? ' reverse' : '',
		name = options.transition,
		$from = options.fromPage,
		toScroll = ARK.lastScroll || ARK.defaultHomeScroll,
		screenHeight = window.innerHeight,
		none = !name || name === 'none',

	toggleViewportClass = function () {
		$('body').toggleClass('viewport-' + name);
	},
	cleanFrom = function () {
		$from
			.removeClass('ui-page-active jqm-out jqm-in reverse ' + name)
			.height('');
	},
	startOut = function () {
		// Due to addition of pointer events, there are odd textbox focus issues when transitioning to queue page on ios tablets, so work around.
		var comparePageTablet = ($to.hasClass('compare') && ARK.isDeviceTabletOnly());

		// If it's not sequential, call the doneOut transition to start the TO page animating in simultaneously
		if (!sequential) {
			doneOut();
		} else {
			$from.animationComplete(doneOut);
		}

		// Set the from page's height and start it transitioning out
		// Note: setting an explicit height helps eliminate tiling in the transitions
		if (comparePageTablet) {
			setTimeout(function () {
				$from
				.height(screenHeight + $(window).scrollTop())
				.addClass(name + ' jqm-out' + reverseClass);
			}, 160);
		} else {
			$from
				.height(screenHeight + $(window).scrollTop())
				.addClass(name + ' jqm-out' + reverseClass);
		}
	},
	doneOut = function () {
		if ($from && sequential) {
			cleanFrom();
		}

		startIn();
	},
	focusPage = function (page) {
		var autofocus = page.find('[autofocus]'),
			pageTitle = page.find('.ui-title:eq(0)');

		if (autofocus.length) {
			autofocus.focus();
			return;
		}

		if (pageTitle.length) {
			pageTitle.focus();
		} else {
			page.focus();
		}
	},
	startIn = function () {
		window.scrollTo(0, toScroll);

		$to.addClass('ui-page-active');

		// Send focus to page as it is now display: block
		focusPage($to);

		// Set to page height
		$to.height(screenHeight);

		if (!none) {
			$to.animationComplete(doneIn);
		}

		$to.addClass(name + ' jqm-in' + reverseClass);

		if (none) {
			doneIn();
		}
	},
	doneIn = function () {
		if (!sequential) {
			if ($from) {
				cleanFrom();
			}
		}

		$to
			.removeClass('jqm-out jqm-in reverse ' + name)
			.height('');

		toggleViewportClass();

		// In some browsers (iOS5), 3D transitions block the ability to scroll to the desired location during transition
		// This ensures we jump to that spot after the fact, if we aren't there already.
		if ($(window).scrollTop() !== toScroll) {
			window.scrollTo(0, toScroll);
		}
	};

	toggleViewportClass();

	if ($from && !none) {
		startOut();
	} else {
		doneOut();
	}
};

ARK.transitionCss = function ($next, options) {
	var isAnimating = false,
		endCurrPage = false,
		endNextPage = false,
		outClass = '',
		inClass = '';

	function init() {
		if (isAnimating) {
			return false;
		} else {
			nextPage();
		}
	}

	function nextPage() {
		var $currPage, $nextPage;

		isAnimating = true;

		$currPage = options.fromPage;
		$nextPage = $next;
		$nextPage.css('z-index', '99999');
		$currPage.css('z-index', '0');

		if (options.transition !== 'none') {
			if (options.reverse) {
				inClass = 'pt-page-moveFromLeft';
				outClass = 'pt-page-moveToRight';
			} else {
				inClass = 'pt-page-moveFromRight';
				outClass = 'pt-page-moveToLeft';
			}

			$currPage.addClass(outClass).one('animationend webkitAnimationEnd', function () {
				$(this).removeClass('ui-page-active');
				endCurrPage = true;
				if (endNextPage) {
					onEndAnimation($currPage, $nextPage);
				}
			});

			$nextPage.addClass(inClass).addClass('ui-page-active').one('animationend webkitAnimationEnd', function () {
				endNextPage = true;
				if (endCurrPage) {
					onEndAnimation($currPage, $nextPage);
				}
			});
		} else {
			$nextPage.addClass('ui-page-active');
			onEndAnimation($currPage, $nextPage);
			$currPage.removeClass('ui-page-active');
		}
	}

	function onEndAnimation($outpage, $inpage) {
		endCurrPage = false;
		endNextPage = false;
		resetPage($outpage, $inpage);
		isAnimating = false;
	}

	function resetPage($outpage, $inpage) {
		$outpage.removeClass(outClass).removeClass('ui-page-active');
		$inpage.removeClass(inClass);
	}

	init();
};

