ARK.updatePageAndShow = function (page, options, pageDetails) {
	var parameterString = pageDetails.parameterString,
		toPageName = pageDetails.params.page;

	ARK.history.setCurrentPage(parameterString);
	ARK.showDeviceDiv(pageDetails.name);

	// Set z-index on pages to avoid text flash issue on iOS
	page.css('z-index', '0');
	options.fromPage.css('z-index', '99999');

	if (ARK.isHoneycomb() || (ARK.isDevicePhoneOnly() && (ARK.isIceCreamSandwich() || ARK.isGingerbread()))) {
		// Transitions look bad on Honeycomb, so just don't use any
		// Transitions look bad on ICS and GB phones when there is content outside the screen,
		// so just don't use any
		options.transition = 'none';
	}

	// TODO: consolidate to one version?
	if (ARK.isWindows8()) {
		// SG - TODO: Need to fix Windows 8 transitions
		// ADM - TODO: 2014-12-19 Are they fixed now?
		ARK.transitionCss(page, options);
	}
	// TODO: Do the transitions need to switch implementation based on the view showing?
	else if (ARK.isAndroid() && ARK.isDeviceTabletOnly()) {
		// The jQuery touch transitions work on Android tablets, but crash randomly on
		// Android phones.
		ARK.transitionJqt(options.fromPage, page,
			options.transition !== 'none' ? options.transition + 'left' : options.transition,
			options.reverse);
	} else {
		ARK.transitionJqm(page, options);
	}

	// Delayed loading of badges and search data to improve start up time
	if (ARK.firstLoad === true) {
		ARK.firstLoad = false;
		setTimeout(ARK.loadSearchData);
		setTimeout(ARK.app.loadBadges);
	}

	if (ARK.isIceCreamSandwich()) {
		if (toPageName === 'top-level' && ARK.isDevicePhoneOnly()) {
			// This is an ugly, ugly hack to force the header to show on the first page
			setTimeout(ARK.forceRedraw, 100, page);
		}

		if (toPageName === 'categories' && ARK.isDeviceTabletOnly()) {
			// This is an ugly, ugly hack to force the first page to show
			setTimeout(ARK.forceRedraw, 100, page);
		}
	}

	if (ARK.promptNoLangDatabase) {
		ARK.promptUpdate(ARK.getNextPage(pageDetails.name), pageDetails.name);
	}
};

ARK.loadAppFirstPage = function (nextPage) {
	ARK.phoneHomePage = 'page=top-level';
	if (ARK.isDevicePhoneOnly()) {
		// Phones load top-level
		ARK.changePage(nextPage + '?' + ARK.phoneHomePage, '#splash', { transition: 'none' });
	} else {
		// Need to calculate which page exactly should be shown
		ARK.buildCategoryNav(nextPage);
	}
};

ARK.buildCategoryNav = function (nextPage, topLevelId, longNameId) {
	var topRow, row;

	ARK.getTopLevelList(function (rs) {
		if (topLevelId === undefined) {
			// Always implicitly choose the first top-level category
			topRow = ARK.checkForRow(rs, null, { params: { page: 'splash' }, parameterString: '#splash:getTopLevelList' });
			if (topRow === null) {
				ARK.tabletHomePage = 'page=categories&id=-1&longNameId=-1&parentId=-1';
				if (ARK.showingTabletView()) {
					ARK.changePage(nextPage + '?' + ARK.tabletHomePage, '#splash', { transition: 'none' });
				} else {
					ARK.changePage(nextPage + '?' + ARK.phoneHomePage, '#splash', { transition: 'none' });
				}
				return;
			}
			ARK.getCategories(topRow.pkCategoryID, topRow.fkLongNameID, function (rs) {
				// Again, always implicitly choose the first category
				row = ARK.checkForRow(rs, null, { params: { page: 'splash' }, parameterString: '#splash:getCategories' });
				if (row === null) {
					ARK.tabletHomePage = 'page=categories&id=-1&longNameId=-1&parentId=-1';
				} else {
					ARK.tabletHomePage = 'page=categories&id=' + row.pkCategoryID + '&longNameId=' + row.fkLongNameID + '&parentId=' + topRow.pkCategoryID;
				}
				if (ARK.showingTabletView()) {
					ARK.changePage(nextPage + '?' + ARK.tabletHomePage, '#splash', { transition: 'none' });
				} else {
					ARK.changePage(nextPage + '?' + ARK.phoneHomePage, '#splash', { transition: 'none' });
				}
			});
		} else {
			// Load a specific top-level category
			ARK.getCategories(topLevelId, longNameId, function (rs) {
				var categoryPage;

				row = ARK.checkForRow(rs, null, { params: { page: 'splash' }, parameterString: '#splash:getCategories' });
				if (row === null) {
					categoryPage = 'page=categories&id=-1&longNameId=-1&parentId=-1';
				} else {
					categoryPage = 'page=categories&id=' + row.pkCategoryID + '&longNameId=' + row.fkLongNameID + '&parentId=' + topLevelId;
				}
				if (ARK.showingTabletView()) {
					ARK.changePage(nextPage + '?' + categoryPage, '#splash', { transition: 'none' });
				} else {
					ARK.changePage(nextPage + '?' + ARK.phoneHomePage, '#splash', { transition: 'none' });
				}
			});
		}
	});
};

ARK.databaseCheckComplete = function (nextPage) {
	var eventName, eventParams, dateDataUpdateAvailable;

	if (ARK.debug) { alert('database check complete'); }

	// Log event - device
	eventName = 'device';
	eventParams = [ 'language', ARK.settings.language, 'language-preference', ARK.getLanguagePreference(), 'platform', ARK.devicePlatform(),
		'local-timestamp', $.format.date(new Date(), "yyyy/MM/dd HH:mm:ss"), 'network-connection', ARK.connectionType(),
		'screen-size', ARK.deviceSize.height + 'x' + ARK.deviceSize.width ];
	if (ARK.updateAvailable) {
		dateDataUpdateAvailable = window.localStorage.getItem('date-data-update-available');

		if (dateDataUpdateAvailable === null || dateDataUpdateAvailable === '') {
			dateDataUpdateAvailable = $.format.date(new Date(), "yyyy/MM/dd HH:mm:ss");
		}
		eventParams.push('date-data-update-available', dateDataUpdateAvailable);
	}
	ARK.loadAppFirstPage(nextPage);
};

ARK.databaseUpdateComplete = function (nextPage) {
	// For some TBD reason, Windows 8 needs extra time to complete previous steps, so wait
	if (ARK.isWindows8()) {
		setTimeout(ARK.updateQueueIds, 0, ARK.loadAppFirstPage, nextPage);
	} else {
		ARK.updateQueueIds(ARK.loadAppFirstPage, nextPage);
	}
	// Log event - data update complete
	ARK.setLocalStorageItem('date-data-update-available', '');
};

ARK.initializePage = function (pageDetails) {
	var page, transition;

	$(pageDetails.name).remove();

	page = $('<div id="' + pageDetails.name.slice(1) + '" class="' + pageDetails.params.page + ' ui-page"><div class="transition hidden"></div></div>').insertAfter('#splash');
	page.append($('#page-template').html());
	// Consider a responsive device as "tablet" for CSS purposes
	if (ARK.isDevicePhoneOnly()) {
		page.find('.wrapper').addClass('phone');
	} else if (ARK.isDeviceTabletOnly()) {
		page.find('.wrapper').addClass('tablet');
	} else if (ARK.showingPhoneView()) {
		page.find('.wrapper').addClass('phone');
	} else {
		page.find('.wrapper').addClass('tablet');
	}

	transition = page.find('.transition');
	ARK.setClickEvent(transition, function (event) {
		// Throw away any clicks on the transition div
		event.stopImmediatePropagation();
		event.preventDefault();
		return false;
	});

	if (ARK.isPortrait()) {
		transition.height(ARK.deviceSize.height).width(ARK.deviceSize.width);
		page.addClass('portrait');
	} else {
		transition.height(ARK.deviceSize.width).width(ARK.deviceSize.height);
		page.addClass('landscape');
	}

	// TODO: Does this need to be dynamic so transitions work when showing tablet, but not showing phone (see also updatePageAndShow)
	if (ARK.isAndroid() && ARK.isDeviceTabletOnly()) {
		transition.addClass('visible');
	}

	// Load template/header
	ARK.loadPageTemplateAndHeader(page, pageDetails);

	return page;
};

ARK.buildSplashPage = function (pageDetails) {
	var translation;
	if (pageDetails.params.reload !== undefined) {
		translation = ARK.translations.get('Checking');
		$('#message').html(translation);
		$('#progress-bar').hide();
	} else {
		translation = ARK.translations.get('Updating');
		$('#message').html(translation);
		$('#progress-bar').show();
	}

	ARK.history.setCurrentPage('#splash');
	$('body > div.ui-page').removeClass('ui-page-active');
	$('#splash').addClass('ui-page-active');
	ARK.setupSplashScreen();

	if (pageDetails.params.reload === '1') {
		ARK.startAppWithDatabaseUpdateCheck('#a', 'Change language');
	} else if (pageDetails.params.reload === '2') {
		ARK.startAppWithDatabaseUpdateCheck('#a', 'Resume data check');
	} else {
		ARK.updateDatabase('#a', pageDetails.params.forceFullDatabase);
	}
};

