			if ($input.attr("name") === "lastname") {
				offset = $('#firstname').offset();
			}
			if ($input.attr("name") === "email") {
				offset = $('#lastname').offset();
			}
			if ($input.attr("name") === "comments") {
				offset = $('#email').offset();
			}
			if (window.innerWidth === $('.menu-feedback').width()) {
				$('.ui-grid-a').hide();
			}
			blankSpaceHeight = 280;
			scrollOffset = 25;
