ARK.splitProductName = function (productName) {
	var result = {},
		lastOpeningParen = productName.lastIndexOf('(');

	if (lastOpeningParen > -1) {
		result.name = productName.substring(0, lastOpeningParen);
		result.parentheticalPart = productName.substring(lastOpeningParen);
	} else {
		result.name = productName;
	}
	return result;
};

ARK.createBadgeImg = function (processorBrandId) {
	// TODO: Maybe add check to see if badge exists, and if not then don't create the <img> tag
	return '<img src="' + ARK.settings.badgePath + processorBrandId + (ARK.getDevicePixelRatio() > 1 ? '_128.gif" />' : '_64.gif" />');
};

ARK.getProductPageLink = function (row, productId, nextPage, isCodeName) {
	var pageLink = nextPage;

	if (isCodeName) {
		// This is only used in search
		if (ARK.isDeviceTabletOnly()) {
			pageLink += '?page=products&id=' + row.CodeNameCategoryID + '&type=2&arkid=' + row.CodeNameID + '&parentId=' + row.fkParentID + '&longNameId=' + row.fkLongNameID;
		} else if (ARK.isDevicePhoneOnly()) {
			pageLink += '?page=products&id=' + row.CodeNameCategoryID + '&type=2&arkid=' + row.CodeNameID;
		} else {
			pageLink = function () {
				if (ARK.showingTabletView()) {
					return nextPage + '?page=products&id=' + row.CodeNameCategoryID + '&type=2&arkid=' + row.CodeNameID + '&parentId=' + row.fkParentID + '&longNameId=' + row.fkLongNameID;
				} else {
					return nextPage + '?page=products&id=' + row.CodeNameCategoryID + '&type=2&arkid=' + row.CodeNameID;
				}
			};
		}
		return pageLink;
	}

	if (ARK.isDeviceTabletOnly()) {
		pageLink += '?page=specs&id=' + productId;
	} else if (ARK.isDevicePhoneOnly()) {
		pageLink += '?page=product&id=' + productId;
	} else {
		pageLink = function () {
			if (ARK.showingTabletView()) {
				return nextPage + '?page=specs&id=' + productId;
			} else {
				return nextPage + '?page=product&id=' + productId;
			}
		};
	}
	return pageLink;
};

ARK.createProductRowReversed = function (row, hasBadge, isCodeName) {
	// <li> looks like: <a>linktext <img> (icon)</a> <a><img> (icon)</a>
	var productId = row.pkProductID || row.fkProductID,
		splitName = ARK.splitProductName(row.Name),
		rowMarkup = '<li class="product-name' + (isCodeName ? ' codename">' : '">');

	if (!isCodeName) {
		if (ARK.inQueue(productId)) {
			rowMarkup += '<a class="removefromqueue" value="' + productId + '"><span></span></a>';
		} else {
			rowMarkup += '<a class="addtoqueue" value="' + productId + '"><span></span></a>';
		}
	}

	if (splitName.parentheticalPart) {
		rowMarkup += '<a class="product-description">' + (hasBadge ? ARK.createBadgeImg(row.ProcessorBrandId) : '') + '<span class="product-description-name">' + splitName.name + '</span><span class="parenthetical">' + splitName.parentheticalPart + '</span></a>';
	} else {
		rowMarkup += '<a class="product-description">' + (hasBadge ? ARK.createBadgeImg(row.ProcessorBrandId) : '') + '<span class="product-description-name">'+ splitName.name + '</span></a>';
	}
	rowMarkup += '</li>';
	return rowMarkup;
};

ARK.createProductRow = function (row, hasBadge, isCodeName) {
	// <li> looks like: <a>linktext <img> (icon)</a> <a><img> (icon)</a>
	var productId = row.pkProductID || row.fkProductID,
		splitName = ARK.splitProductName(row.Name),
		rowMarkup = '<li class="product-name' + (isCodeName ? ' codename">' : '">');

	if (splitName.parentheticalPart) {
		rowMarkup += '<a class="product-description">' + (hasBadge ? ARK.createBadgeImg(row.ProcessorBrandId) : '') + '<span class="product-description-name">' + splitName.name + '</span><span class="parenthetical">' + splitName.parentheticalPart + '</span></a>';
	} else {
		rowMarkup += '<a class="product-description">' + (hasBadge ? ARK.createBadgeImg(row.ProcessorBrandId) : '') + '<span class="product-description-name">'+ splitName.name + '</span></a>';
	}

	if (!isCodeName) {
		if (ARK.inQueue(productId)) {
			rowMarkup += '<a class="removefromqueue" value="' + productId + '"><span></span></a>';
		} else {
			rowMarkup += '<a class="addtoqueue" value="' + productId + '"><span></span></a>';
		}
	}
	rowMarkup += '</li>';
	return rowMarkup;
};

ARK.toggleQueue = function (id, name, arkId, element, fromPage) {
	if (ARK.queue.length === ARK.MAX_QUEUE_SIZE && !ARK.inQueue(id)) {
		ARK.alert(ARK.translations.get('QueueFull'), ARK.translations.get('OK'));
		return;
	}

	ARK.toggleProductInQueue(id, name, arkId, fromPage);
	// Due to a consistent issue on android and an occasional issue on ios where queue number doesn't display, redraw page
    ARK.forceRedraw($('.ui-page-active'));
	$(element).toggleClass('removefromqueue').toggleClass('addtoqueue');
};

ARK.createQueueCallbackFunction = function (id, name, arkId, fromPage) {
	return function () { ARK.toggleQueue(id, name, arkId, this, fromPage); };
};

ARK.androidNonAppLinkClick = function (event) {
	event.preventDefault();
	event.stopImmediatePropagation();
	navigator.app.loadUrl($(this).attr('href'), { openExternal: true });
};

ARK.iOSNonAppLinkClick = function (event) {
	event.preventDefault();
	event.stopImmediatePropagation();
	window.open($(this).attr('href'), '_system');
};

ARK.addDisclaimers = function (page, disclaimersType) {
	function setDisclaimersMarkup() {
		var disclaimersList = page.find('.disclaimers-list');

		disclaimersList.html(ARK.dbCache['disclaimerListMarkup' + disclaimersType]);

		if (ARK.isAndroid()) {
			disclaimersList.find('a').click(ARK.androidNonAppLinkClick);
		} else if (ARK.isiOS()) {
			disclaimersList.find('a').click(ARK.iOSNonAppLinkClick);
		} else {
			disclaimersList.find('a').click(function (event) { event.stopImmediatePropagation(); });
		}
	}

	page.find('.disclaimers').html($('#disclaimers-structure').html());
	page.find('span.disclaimers-label').html(ARK.translations.get('Disclaimers'));
	if (ARK.dbCache['disclaimerListMarkup' + disclaimersType] !== undefined) {
		setDisclaimersMarkup();
	}

	page.find('.disclaimers-body').hide(); // Disclaimers start hidden

	ARK.setClickEvent(page.find('.disclaimers-header'), function () {
		if (ARK.dbCache['disclaimerListMarkup' + disclaimersType] === undefined) {
			ARK.app.getDisclaimerData(disclaimersType, function () {
				ARK.logGoogleAnalyticsEvents('Components', 'click:disclaimers', 'disclaimerListMarkup' + disclaimersType);

				setDisclaimersMarkup();
				page.find('li.disclaimers-body').toggle();
				page.find('span.disclaimers-toggle').toggleClass('open');
			});
		} else {
			ARK.logGoogleAnalyticsEvents('Components', 'click:disclaimers', 'disclaimerListMarkup' + disclaimersType);

			page.find('li.disclaimers-body').toggle();
			page.find('span.disclaimers-toggle').toggleClass('open');
		}
	});
	// Removed Android special case. If it's still an issue then we'll try keeping the toggle, but
	// use transparent 0 instead of display none
		// There's a strange scrolling issue related to toggling disclaimers
		// on Android tablets. So we just always show the disclaimers on Android
		// tablets and hide the toggle icon
};

ARK.buildProductsPage = function (pageDetails, options) {
	var page = ARK.initializePage(pageDetails);

	// Populate content
	ARK.getProducts(pageDetails.params.id, pageDetails.params.type, pageDetails.params.arkid, function (rs) {
		// The first row in the displayed list is the category name + any filter applied
		var row, list, subheader, name, nextPage, i, j, lastLi, pageLink, localCode, pageName;

		row = ARK.checkForRow(rs, page, options, pageDetails);
		if (row === null) {
			return;
		}

		// Log page view in Google Analytics
		localCode = ARK.settings.GALocalCode;
		pageName = '/mobileapp/ark/' + localCode + '/' +  pageDetails.params.page + '/' + pageDetails.params.arkid + '/' + row.CategoryName.replace(/®|™|,|\)|\(/g, '').replace(/ /g, '-').replace('.', '_');
		ARK.logGoogleAnalyticsPageView(pageName);

		list = page.find('.products-list');
		subheader = page.find('.products-list-header');

		name = row.CategoryName;
		nextPage = ARK.getNextPage(pageDetails.name);

		if (row.FilterName === undefined || row.FilterName === null) {
			subheader.append('<div class="ui-li-divider product-list-divider"><span class="product-list-title">' + name + '</span><div class="clear"></div></div>');
		} else {
			subheader.append('<div class="ui-li-divider product-list-divider"><span class="product-list-title">' + name + '<span class="filter">(' + row.FilterName + ')</span></span><div class="clear"></div></div>');
		}

		for (i = 0; i < rs.rows.length; i++) {
			row = rs.rows.item(i);
			if (!ARK.isDevicePhoneOnly() && ARK.isIceCreamSandwich()) {
				list.append(ARK.createProductRowReversed(row));
			} else {
				list.append(ARK.createProductRow(row));
			}
			pageLink = ARK.getProductPageLink(row, row.pkProductID || row.fkProductID, nextPage);
			for (j = 0; j < list.length; j++) {
				lastLi = $(list[j]).find('li').last();
				ARK.setClickEvent(lastLi.find('a.addtoqueue, a.removefromqueue'), ARK.createQueueCallbackFunction(row.pkProductID, row.Name, row.ARKID, 'products'));
				ARK.setLink(lastLi.find('a.product-description'), pageLink, pageDetails.name, { transition: 'slide' });
			}
		}

		ARK.addDisclaimers(page, ARK.MULTI_DISCLAIMERS);

		if (!ARK.isDevicePhoneOnly()) {
			// If there isn't a previous category (some codenames aren't in the hierarchy), then show nothing in the nav
			if (pageDetails.params.parentId === 'undefined' || pageDetails.params.parentId === 'null' || pageDetails.params.longNameId === 'undefined' || pageDetails.params.longNameId === 'null') {
				ARK.updatePageAndShow(page, options, pageDetails);
				return;
			}
			// Get the previous category page and load it into the nav
			ARK.getCategories(pageDetails.params.parentId, pageDetails.params.longNameId, function (rs) {
				var nextPage, i, highlightClass, row, categoryName,
					// The first row in the displayed list is the category name
					list = page.find('.tablet-nav-list'),
					subhead = page.find('.tablet-nav-subheader'),
					currentCategoryID = parseInt(pageDetails.params.id, 10),
					highlightedRow, topRow, position;

				row = ARK.checkForRow(rs, page, options, pageDetails);
				if (row === null) {
					return;
				}
				categoryName = row.Value;
				subhead.append(ARK.createTitleRow(categoryName, row.TopLevelCategory));

				nextPage = ARK.getNextPage(pageDetails.name);
				for (i = 0; i < rs.rows.length; i++) {
					row = rs.rows.item(i);
					// Highlight current category
					highlightClass = row.pkCategoryID === currentCategoryID ? ' highlighted' : '';
					pageLink = nextPage + '?page=products&id=' + row.pkCategoryID + '&type=' + row.Type + '&arkid=' + row.ARKID + '&parentId=' + pageDetails.params.parentId + '&longNameId=' + pageDetails.params.longNameId;
					list.append('<li class="product-list-row"><a><span class="category-title' + highlightClass + '">' + row.Name + '</span><span class="product-count">' + row.ProductCount + '</span></a></li>');
					ARK.setLink(list, pageLink, pageDetails.name, { transition: 'none', navLink: true });
				}

				// Set up hooks for changing highlighting
				ARK.setClickEvent(list.find('li'), function (e) {
					if ($(this).find('span.category-title').hasClass('highlighted')) {
						e.stopImmediatePropagation();
						e.preventDefault();
						return false;
					}

					// Turn off highlighting for current category
					list.find('span.category-title.highlighted').removeClass('highlighted');

					// Turn on highlighted for clicked category
					$(this).find('span.category-title').addClass('highlighted');
				});

				ARK.updatePageAndShow(page, options, pageDetails);

				// Scroll to highlighted row
				highlightedRow = page.find('.product-list-row .highlighted');
				topRow = page.find('.product-list-row .category-title').first();
				position = highlightedRow.offset().top - topRow.offset().top;
				page.find('.tablet-nav .ui-content').scrollTop(position);
			});
		} else {
			ARK.updatePageAndShow(page, options, pageDetails);
		}
	});
};

ARK.createProductNameRow = function (product) {
	var row = '<span class="product-name">',
		splitName = ARK.splitProductName(product.ProductName);

	if (product.HasBadge) {
		row += ARK.createBadgeImg(product.ProcessorBrandId);
	}
	row += ARK.formatSplitProductName(splitName);
	row += '</span><div class="clear"></div>';

	return row;
};

// Phone-only page
ARK.buildProductPage = function (pageDetails, options) {
	var page = ARK.initializePage(pageDetails);

	// Populate content
	ARK.getProduct(pageDetails.params.id, function (rs) {
		var product, codenameList, productList, subhead, localCode, pageName;

		product = ARK.checkForRow(rs, page, options, pageDetails);
		if (product === null) {
			return;
		}

		// Log page view in Google Analytics
		localCode = ARK.settings.GALocalCode;
		pageName = '/mobileapp/ark/' + localCode + '/' + pageDetails.params.page + '/' + product.ARKID + '/' + product.ProductName.replace(/®|™|,|\)|\(/g, '').replace(/ /g, '-').replace('.', '_');
		ARK.logGoogleAnalyticsPageView(pageName);

		// Add product name row
		subhead = page.find('.product-list-header');
		subhead.html(ARK.createProductNameRow(product));
		productList = page.find('.product-list');
		ARK.createProductLinksList(productList, pageDetails, product, { transition: 'slide', navLink: false });

		codenameList = page.find('.codename-list');
		if (product.CodeNameID !== null) {
			codenameList.append('<li class="product-list-row"><a><span class="product-name">' + ARK.translations.get('ProductsFormerly').replace('CODE_NAME', product.CodeName) + '</span></a></li>');
			ARK.setLink(codenameList, ARK.getNextPage(pageDetails.name) + '?page=products&id=' + product.CodeNameCategoryID + '&type=2&arkid=' + product.CodeNameID, pageDetails.name, { transition: 'slide' });
		}

		// NOTE: setting up the page footer results in the page being displayed via updatePageAndShow
		ARK.setupProductPageFooter(page, product.pkProductID, product, pageDetails, options);

		if (ARK.isDeviceResponsive()) {
			$(window).on('resize.page', function () {
				if (ARK.showingTabletView()) {
					if ($('.search-popup:visible').length > 0) {
						// Change page only after menu is closed
						$(document).off('click', '#closesearch').one('click', '#closesearch', function () {
							ARK.searchClose(page);
							ARK.changePage(ARK.getNextPage(pageDetails.name) + '?page=specs&id=' + pageDetails.params.id, pageDetails.name, { transition: 'none'});
						});
					} else {
						ARK.changePage(ARK.getNextPage(pageDetails.name) + '?page=specs&id=' + pageDetails.params.id, pageDetails.name, { transition: 'none'});
					}
				}
			});
		}
	});
};

ARK.setupProductPageFooter = function (page, productId, product, pageDetails, options) {
	var queue;

	// productId can be a string
	productId = parseInt(productId, 10);

	// Add mask for pop-ups
	page.find('.phone').append('<div class="modal-mask hidden"></div>');

	// Create the footer HTML
	ARK.createFooter(page, pageDetails.params.page !== 'product');

	// Set the icon/binding for queue button in footer
	queue = page.find('.ui-footer .modify-queue');

	if (ARK.inQueue(productId)) {
		queue.addClass('removefromqueue');
	} else {
		queue.addClass('addtoqueue');
	}
	queue.prop('value', productId);
	ARK.setClickEvent(queue, ARK.createQueueCallbackFunction(productId, product.ProductName, product.ARKID, 'product'));

	// Set up the share pop-up
	ARK.createSharePopup(page, product.ARKID, product.ProductName);

	// Set up the product details links pop-up
	if (pageDetails.params.page !== 'product') {
		ARK.createProductLinksPopup(page, pageDetails, product);
	}

	// Set up the external links pop-up
	// NOTE: this is where updatePageAndShow is called
	ARK.createExternalLinksPopup(page, pageDetails, options, product.ARKID);
};

ARK.createProductSectionRow = function (sectionResourceName) {
	return '<span class="section-name">' + ARK.translations.get(sectionResourceName) + '</span>';
};

ARK.createToolTipDiv = function () {
	return '<div class="tooltip-popup hidden"><div class="header hidden"><div class="header-image">Intel<span class="reg">®</span> ARK</div><a class="close-link"></a></div><div class="tooltip-title"></div><div class="tooltip-description"></div></div>';
};

ARK.createCloseTabletTooltipPopup = function (page) {
	return function () {
		page.find('.modal-mask').hide();
		page.find('.tooltip-popup').hide();
	};
};

// This is only called for tablet
ARK.buildToolTip = function (page) {
	ARK.setClickEvent(page.find('.tooltip-popup .header .close-link').html('<span class="close-icon"></span>'), ARK.createCloseTabletTooltipPopup(page));
	page.find('.tooltip-popup .header').show();
};

ARK.buildSpecsPage = function (pageDetails, options) {
	var page = ARK.initializePage(pageDetails);

	page.find('.wrapper').append(ARK.createToolTipDiv());
	ARK.buildToolTip(page);

	// Populate content
	ARK.getSpecs(pageDetails.params.id, function (rs) {
		var list = page.find('.specs-list'),
			subhead = page.find('.specs-header'),
			// Use first row in the list to get the product name and the section name
			row, sectionName = '', i, relatedProductId, relatedProductSpan, nextPage, link, localCode, pageName;

		function createLinkFunction (nextPage, relatedProductId) {
			return function () {
				if (ARK.showingPhoneView()) {
					return nextPage + '?page=product&id=' + relatedProductId;
				}
				return nextPage + '?page=specs&id=' + relatedProductId;
			};
		}

		row = ARK.checkForRow(rs, page, options, pageDetails);
		if (row === null) {
			return;
		}

		// Log page view in Google Analytics
		localCode = ARK.settings.GALocalCode;
		pageName = '/mobileapp/ark/' + localCode + '/' + pageDetails.params.page + '/' + row.ARKID + '/' + row.ProductName.replace(/®|™|,|\)|\(/g, '').replace(/ /g, '-').replace('.', '_');
		ARK.logGoogleAnalyticsPageView(pageName);

		if (!ARK.isDeviceTabletOnly()) {
			// Add product name row
			subhead.append(ARK.createProductNameRow(row));
		}

		// Add section name row
		subhead.append(ARK.createProductSectionRow('Specifications'));

		nextPage = ARK.getNextPage(pageDetails.name);
		// Specs
		for (i = 0; i < rs.rows.length; i++) {
			row = rs.rows.item(i);
			// Section name
			if (row.Section !== sectionName) {
				list.append('<li class="specs-heading">' + row.Section + '</li>');
				sectionName = row.Section;
			}
			list.append(ARK.createSpecRow(row));
			if (!!row.ToolTip) {
				ARK.setClickEvent(list.find('li').last(), ARK.createToolTipCallbackFunction(page, row.Name, row.ToolTip));
			}
			if (row.Value.indexOf('data-product-id') !== -1) {
				relatedProductId = list.find('li').last().find('a').attr('data-product-id');
				relatedProductSpan = list.find('li').last().find('.related-product');
				if (ARK.isDevicePhoneOnly()) {
					link =  nextPage + '?page=product&id=' + relatedProductId;
				} else if (ARK.isDeviceTabletOnly()) {
					link =  nextPage + '?page=specs&id=' + relatedProductId;
				} else {
					link = createLinkFunction(nextPage, relatedProductId);
				}
				ARK.setLink(relatedProductSpan, link, pageDetails.name, { transition: 'slide' });
			}
		}

		if (ARK.isDeviceTabletOnly()) {
			// NOTE: setting up the product navgation results in the page being displayed via updatePageAndShow
			ARK.createProductNav(page, pageDetails, options, true);
		} else if (ARK.isDevicePhoneOnly()) {
			// NOTE: setting up the page footer results in the page being displayed via updatePageAndShow
			ARK.setupProductPageFooter(page, pageDetails.params.id, row, pageDetails, options);
		} else {
			// If responsive device, build both product nav and product footer (pass in false as last param to createProductNav, so updatePageAndShow is not called
			ARK.createProductNav(page, pageDetails, options, false);
			ARK.setupProductPageFooter(page, pageDetails.params.id, row, pageDetails, options);
		}
	});
};

ARK.createProductNav = function (page, pageDetails, options, callUpdatePageAndShow) {
	// Delete footer if tablet only
	if (callUpdatePageAndShow) {
		page.find('.tablet .ui-footer').remove();
	}

	// Get the product information and load it into the nav
	ARK.getProduct(pageDetails.params.id, function (rs) {
		var product, navList = page.find('.product-list'), navSubHead = page.find('.product-subheader');

		product = ARK.checkForRow(rs, page, options, pageDetails);
		if (product === null) {
			return;
		}

		// Setup queue toggle in header
		ARK.setClickEvent(page.find('.ui-header .modify-queue-button'), ARK.createQueueCallbackFunction(product.pkProductID, product.ProductName, product.ARKID, 'product'));

		// Add product name row
		navSubHead.html(ARK.createProductNameRow(product));

		ARK.createProductLinksList(navList, pageDetails, product, { navLink: true });

		ARK.getExternalLinks(pageDetails.params.id, product.ARKID, function (rs, arkId) {
			var list = page.find('.extlinks-list'),
				i, row, shareList, codenameList, href, text;

			// Place "view on site" above other external links
			href = ARK.createLocalizedBaseArkLink() + 'products/' + arkId + '?ui=BIG';
			text = ARK.translations.get('ViewOnFullSite');
			list.append('<li class="external-link">' + ARK.createNonAppLink(href, text, 'external-link') + '</li>');

			if (rs !== null) {
				for (i = 0; i < rs.rows.length; i++) {
					row = rs.rows.item(i);
					list.append('<li class="external-link">' + ARK.createNonAppLink(row.Url, row.Text, '') + '</li>');
				}
			}
			
			ARK.setClickEvent($('.extlinks-list li'), function () {
				ARK.logGoogleAnalyticsEvents('Third-party', 'click:exit-Intel', $(this).find('a').attr('data-url'));
			});

			shareList = page.find('.share-list');
			shareList.html('<li><span class="share">' + ARK.translations.get('Share') + '</span><span class="share-toggle"></span></li>');
			ARK.setClickEvent(shareList.find('li'), function () {
				// Log toggling of share menu in Google Analytics
				if ($(this).hasClass('open')) {
					ARK.logGoogleAnalyticsEvents('Navigation', 'nav:menu', 'share_product_close');
				} else {
					ARK.logGoogleAnalyticsEvents('Navigation', 'nav:footer', 'share_product_show');
				}
				$(this).toggleClass('open');
				shareList.find('a').closest('li').toggle();
			});
			shareList.append(ARK.createShareListItems(product.ARKID, product.ProductName));
			shareList.find('a').closest('li').hide();

			shareList.find('li').click(function () {
				// Log event
				var eventName = '';

				if ($(this).is(':contains("Email")')) {
					eventName = 'share-email';
				} else if ($(this).is(':contains("Facebook")')) {
					eventName = 'share-facebook';
				} else if ($(this).is(':contains("LinkedIn")')) {
					eventName = 'share-linkedin';
				} else if ($(this).is(':contains("Twitter")')) {
					eventName = 'share-twitter';
				}
				if (eventName !== '') {
					ARK.logGoogleAnalyticsEvents('Social', eventName.replace('-',':'), 'product:' + arkId);
				}
			});

			codenameList = page.find('.codename-list');

			if (product.CodeNameID !== null) {
				ARK.getCodenameParentCategory(product.CodeNameID, function (rs) {
					var parentCategory, pageLink, list;

					if (rs !== null) {
						parentCategory = rs.rows.length === 0 ? {} : rs.rows.item(0);
						pageLink = ARK.getNextPage(pageDetails.name) + '?page=products&id=' + product.CodeNameCategoryID + '&type=2&arkid=' + product.CodeNameID + '&parentId=' + parentCategory.pkCategoryID + '&longNameId=' + parentCategory.fkLongNameID;
						list = codenameList.append('<li class="product-list-row"><a><span class="product-name">' + ARK.translations.get('ProductsFormerly').replace('CODE_NAME', product.CodeName) + '</span></a></li>');
						// Note: even though this is in the nav, it links to different base page so it's not a nav click (no history logged in tablet view)
						ARK.setLink(list, pageLink, pageDetails.name, { transition: 'slide' });
					}
					if (callUpdatePageAndShow) {
						ARK.updatePageAndShow(page, options, pageDetails);
					}
				});
			} else {
				if (callUpdatePageAndShow) {
					ARK.updatePageAndShow(page, options, pageDetails);
				}
			}
		});
	});
};

ARK.createSpecRow = function (row) {
	var rowMarkup,
		name = row.Name,
		value = row.Value;

	if (!!row.ToolTip) {
		rowMarkup = '<li class="tool-tip spec-row"><span class="spec-name">' + name + '<span class="tool-tip-icon"></span></span>';
	} else {
		rowMarkup = '<li class="spec-row"><span class="spec-name ">' + name + '</span>';
	}

	if (value.indexOf('data-product-id') !== -1) {
		rowMarkup += '<span class="related-product spec-value">' + value + '<span class="link-icon"></span></span>';
	} else {
		rowMarkup += '<span class="spec-value">' + value + '</span>';
	}

	rowMarkup += '</li>';
	return rowMarkup;
};

ARK.createToolTipCallbackFunction = function (page, name, toolTip) {
	return function () {
		ARK.showToolTip(page, name, toolTip);
	};
};

ARK.showToolTip = function (page, name, toolTip) {
	// Log viewing of tool tip in Google Analytics
	ARK.logGoogleAnalyticsEvents('Components', 'click:tool_tip', name.replace(/®|™|,|\)|\(/g, '').replace(/ /g, '-').replace('.', '_'));

	var tooltip = page.find('.tooltip-popup');

	page.find('.tooltip-popup .tooltip-title').html(name);
	page.find('.tooltip-popup .tooltip-description').html(toolTip);

	// Set the location
	if (ARK.showingTabletView() && window.innerHeight > 580 && window.innerWidth > 320) {
		tooltip.css('height', '580px').css('width', '320px').addClass('no-kink');
		tooltip.css('top', $(window).height() / 2 - tooltip.height() / 2).css('left', $(window).width() / 2 - tooltip.width() / 2);
	} else {
		tooltip.removeAttr('style').removeClass('no-kink');
	}

	page.find('.modal-mask').fadeIn('fast');
	if (!ARK.isDeviceTabletOnly()) {
		page.attr('data-scroll', $('body').scrollTop());
	}
	page.find('.tooltip-popup').show();
};

