ARK.checkDatabaseUpdate = function (reason, callback) {
	if (ARK.hasConnection()) {
		$.ajax(ARK.app.getCurrentHashUrl())
		.done(function (data) {
			var currentHash = ARK.app.getCurrentHash(data),
				deviceHash = window.localStorage.getItem('hash-' + ARK.settings.language);

			window.localStorage.setItem('lastUpdateCheckTimestamp-' + ARK.settings.language, ARK.app.getDate(new Date()));
			ARK.databaseForLangMissing = false;

			if (deviceHash === null) {
				// First time, set deviceHash to the default db hash if using English
				if (ARK.settings.language === 'en') {
					deviceHash = ARK.settings.appDatabaseHash;
					ARK.setLocalStorageItem('hash-en', deviceHash);
					ARK.updateAvailable = currentHash !== deviceHash;
					callback('first');
				} else {
					// If the deviceHash is still null, then there is an "update" (the first copy of the database in non-English)
					ARK.updateAvailable = true;
					ARK.databaseForLangMissing = true;
					ARK.promptNoLangDatabase = true;
					callback('first-not-english');
				}
			} else {
				// The user has completed at least one update. If auto update is set to daily/weekly then do that now.
				ARK.updateAvailable = currentHash !== deviceHash;
				callback('at-least-one');
			}
		})
		.fail(function (jqXHR, textStatus, errorThrown) {
			ARK.logError('Failed to get current db hash in checkDatabaseUpdate (' + reason + '). url=' + this.url + ', status=' + jqXHR.status + ' (' + jqXHR.statusText + '), errorThrown=' + errorThrown + ', textStatus=' + textStatus + ', jqXHR=' + JSON.stringify(jqXHR));
		})
		.always(function (jqXHR, textStatus) {
			if (textStatus !== 'success') {
				ARK.updateAvailable = false;
				callback('failed');
			}
		});
	} else {
		ARK.updateAvailable = false;
		callback('no connection');
	}
};

ARK.shouldAutoUpdate = function () {
	var dataUpdatePreference, lastUpdateTimestamp, now;

	dataUpdatePreference = ARK.getDataUpdatePreference();
	lastUpdateTimestamp = window.localStorage.getItem('lastUpdateTimestamp-' + ARK.settings.language);
	lastUpdateTimestamp = lastUpdateTimestamp ? new Date(lastUpdateTimestamp) : new Date();
	now = new Date();

	return (dataUpdatePreference === 'daily' && (now.getTime() - lastUpdateTimestamp.getTime() > ARK.ONE_DAY_MS) ||
		dataUpdatePreference === 'weekly' && (now.getTime() - lastUpdateTimestamp.getTime() > ARK.ONE_WEEK_MS));
};

ARK.startAppWithDatabaseUpdateCheck = function (nextPage, reason) {
	ARK.checkDatabaseUpdate(reason, function (result) {
		// The user has completed at least one update. If auto update is set to daily/weekly then do that now.
		if (result === 'at-least-one' && ARK.updateAvailable && ARK.shouldAutoUpdate()) {
			$('#message').html(ARK.translations.get('Updating'));
			$('#progress-bar').show();
			ARK.updateDatabase(nextPage);
		} else {
			ARK.databaseCheckComplete(nextPage);
		}
	});
};

ARK.updateDatabase = function (nextPage, forceFullDatabase) {
	var startTotal, startDownload, downloadTime,
		diffsDownloaded = false;

	function downloadDatabase (why) {
		$.ajax(ARK.app.getCurrentDatabaseUrl())
		.done(function (data) {
			var startDecodeWrite,
				database = ARK.app.getDatabase(data);

			downloadTime = new Date() - startDownload;
			startDecodeWrite = new Date();

			ARK.app.writeDatabase(ARK.settings.language, database.data_bin, updateSettings(database.checksum, startDecodeWrite, database.data_bin.length, why), function (err) {
				ARK.logError('Failed to write database. Error=' + err);
				if (err.indexOf('No space left on device') !== -1) {
					ARK.alert(ARK.translations.get('DatabaseNoSpaceError'), ARK.translations.get('OK'));
				}
				ARK.databaseUpdateComplete(nextPage);
			});
		})
		.fail(function (jqXHR, textStatus, errorThrown) {
			ARK.logError('Error in downloadDatabase (getDatabase because ' + why + '). url=' + this.url + ', status=' + jqXHR.status + ' (' + jqXHR.statusText + '), errorThrown=' + errorThrown + ', textStatus=' + textStatus + ', jqXHR=' + JSON.stringify(jqXHR));
		})
		.always(function (jqXHR, textStatus) {
			if (textStatus !== 'success') {
				ARK.alert(ARK.translations.get('DatabaseDownloadFailedError'), ARK.translations.get('OK'));
				ARK.databaseUpdateComplete(nextPage);
			}
		});
	}

	function updateSettings (newChecksum, startTime, downloadSize, type) {
		return function (diffCount) {
			var date = ARK.app.getDate(new Date()),
				endTime = new Date() - startTime,
				totalTime, timingMessage;

			// Store hash
			ARK.setLocalStorageItem('hash-' + ARK.settings.language, newChecksum);
			ARK.setLocalStorageItem('lastUpdateTimestamp-' + ARK.settings.language, date);

			ARK.updateAvailable = false;
			ARK.databaseForLangMissing = false;
			ARK.initializeViewCallback = ARK.databaseUpdateComplete;
			ARK.initializeViewCallbackParameter = nextPage;

			totalTime = new Date() - startTotal;
			timingMessage = 'Update database (' + type + '): download: ' + downloadTime + 'ms (' + downloadSize + ' bytes), application: ' + endTime + 'ms';
			if (diffCount) {
				ARK.logError(timingMessage + ', diff count: ' + diffCount + ', total: ' + totalTime + 'ms', { severity: ARK.LOG_ID });
			} else {
				ARK.logError(timingMessage + ', total: ' + totalTime + 'ms', { severity: ARK.LOG_ID });
			}

			ARK.initializeView();
		};
	}

	if (ARK.hasConnection()) {
		if (ARK.dbState.inProgress()) {
			setTimeout(ARK.updateDatabase, 500, nextPage, forceFullDatabase);
			return;
		}
		startTotal = new Date();
		startDownload = new Date();

		if (ARK.app.db !== null) {
			ARK.app.db.close();
		}

		if (forceFullDatabase) {
			downloadDatabase('forced (' + forceFullDatabase + ')');
			return;
		}

		// First get the current server database hash
		$.ajax(ARK.app.getCurrentHashUrl())
		.done(function (data) {
			var noDiffsReason,
				currentServerHash = ARK.app.getCurrentHash(data),
				deviceHash = window.localStorage.getItem('hash-' + ARK.settings.language);

			// Now, ask for diffs from local database to current server database
			$.ajax(ARK.app.getDiffUrl(deviceHash, currentServerHash)).done(function (data) {
				var diff = ARK.app.getDiff(data),
					startApplyDiffs;

				downloadTime = new Date() - startDownload;
				// Are there any diffs for us?
				if (diff === undefined) {
					noDiffsReason = 'no diffs available';
					return;
				}
				// Yes, there are! Apply them.
				diffsDownloaded = true;
				startApplyDiffs = new Date();
				ARK.app.applyDiffs(ARK.settings.language, diff.data_diff, diff.clientChecksum, updateSettings(diff.endingChecksum, startApplyDiffs, diff.data_diff.length, 'diffs'), function (error) {
					// Applying diff failed. Download the full database
					ARK.logError('Failed to apply diffs. Error=' + error);
					downloadDatabase('diff application failed');
				});
			}).fail(function (jqXHR, textStatus, errorThrown) {
				var parseJSONerror, errorMsg;

				// OData returns 404 if no results found
				if (jqXHR.status === 404) {
					// If responseText is valid JSON then we'll treat this as "no results found" instead of "page not found"
					// and we won't report an error
					try {
						$.parseJSON(jqXHR.responseText);
						noDiffsReason = 'no diffs available';
						return;
					}
					catch (e) {
						parseJSONerror = e.message;
					}
				}
				noDiffsReason = 'diff download failed';
				errorMsg = 'Error in updateDatabase. url=' + this.url + ', ';
				if (parseJSONerror) {
					errorMsg += 'parseJSONerror=' + parseJSONerror + ', ';
				}
				errorMsg += 'status=' + jqXHR.status + ' (' + jqXHR.statusText + '), errorThrown=' + errorThrown + ', textStatus=' + textStatus + ', jqXHR=' + JSON.stringify(jqXHR);
				ARK.logError(errorMsg);
			}).always(function (jqXHR, textStatus) {
				if (textStatus !== 'success' || !diffsDownloaded) {
					// If there was any kind of error downloading diffs, then try to download full database
					downloadDatabase(noDiffsReason);
				}
			});
		})
		.fail(function (jqXHR, textStatus, errorThrown) {
			ARK.logError('Failed to get current db hash in updateDatabase. url=' + this.url + ', status=' + jqXHR.status + ' (' + jqXHR.statusText + '), errorThrown=' + errorThrown + ', textStatus=' + textStatus + ', jqXHR=' + JSON.stringify(jqXHR));
		})
		.always(function (jqXHR, textStatus) {
			if (textStatus !== 'success') {
				downloadDatabase('check current db hash failed');
			}
		});
	} else {
		// TODO: what should happen if user no longer has network connection?
		ARK.databaseUpdateComplete(nextPage);
	}
};

