ARK.gingerbreadTranslate = function (rs, table) {
	var row, i;

	if (ARK.isGingerbread()) {
		for (i = 0; i < rs.rows.length; i++) {
			row = rs.rows.item(i);
			if (table === 'externallinks') {
				if (row.ShowOnCompatible === 'true' || row.ShowOnCompatible === '1') {
					row.ShowOnCompatible = 1;
				} else {
					row.ShowOnCompatible = 0;
				}
			}
			if (table === 'category') {
				row.fkProductID = parseInt(row.fkProductID, 10);
				if (row.fkParentID === undefined) {
					row.fkParentID = null;
				} else {
					row.fkParentID = parseInt(row.fkParentID, 10);
				}
			}
			if (table === 'category' || table === 'codenameproductcategory') {
				if (row.Type === undefined) {
					row.Type = null;
				} else {
					row.Type = parseInt(row.Type, 10);
				}
				row.ProductCount = parseInt(row.ProductCount, 10);
			}
			if (table === 'category' || table === 'codenameproductcategory' || table === 'toplevellist') {
				row.pkCategoryID = parseInt(row.pkCategoryID, 10);
				row.fkLongNameID = parseInt(row.fkLongNameID, 10);
			}
			if (table === 'category' || table === 'products' || table === 'codenameproductcategory') {
				if (row.ARKID === undefined) {
					row.ARKID = null;
				} else {
					row.ARKID = parseInt(row.ARKID, 10);
				}
			}
			if (table === 'products') {
				row.pkProductID = parseInt(row.pkProductID, 10);
			}
			if (table === 'product' || table === 'specs' || table === 'orderspecs' || table === 'pcnmdds' || table === 'compatibleproducts') {
				if (row.HasCompatibleProducts === 'true' || row.HasCompatibleProducts === '1') {
					row.HasCompatibleProducts = 1;
				} else {
					row.HasCompatibleProducts = 0;
				}

				if (row.HasCompatibleChipsets === 'true' || row.HasCompatibleChipsets === '1') {
					row.HasCompatibleChipsets = 1;
				} else {
					row.HasCompatibleChipsets = 0;
				}

				if (row.HasCompatibleProcessors === 'true' || row.HasCompatibleProcessors === '1') {
					row.HasCompatibleProcessors = 1;
				} else {
					row.HasCompatibleProcessors = 0;
				}

				if (row.HasOrderSpec === 'true' || row.HasOrderSpec === '1') {
					row.HasOrderSpec = 1;
				} else {
					row.HasOrderSpec = 0;
				}

				if (row.HasBadge === 'true' || row.HasBadge === '1') {
					row.HasBadge = 1;
				} else {
					row.HasBadge = 0;
				}

				if (row.HasChipsetComponents === 'true' || row.HasChipsetComponents === '1') {
					row.HasChipsetComponents = 1;
				} else {
					row.HasChipsetComponents = 0;
				}

				if (row.HasPcnMdds === 'true' || row.HasPcnMdds === '1') {
					row.HasPcnMdds = 1;
				} else {
					row.HasPcnMdds = 0;
				}
			}
			if (table === 'product') {
				if (row.CodeNameID === undefined) {
					row.CodeNameID = null;
				}
			}
			if (table === 'orderspecs') {
				if (row.Retired === 'true' || row.Retired === '1') {
					row.Retired = 1;
				} else {
					row.Retired = 0;
				}
			}
			if (table === 'pcnmdds') {
				if (row.PcnAvail === 'true' || row.PcnAvail === '1') {
					row.PcnAvail = 1;
				} else {
					row.PcnAvail = 0;
				}

				if (row.MddsAvail === 'true' || row.MddsAvail === '1') {
					row.MddsAvail = 1;
				} else {
					row.MddsAvail = 0;
				}
			}
			if (table === 'comparespecifications') {
				row.ProductID = parseInt(row.ProductID, 10);
				row.SpecificationID = parseInt(row.SpecificationID, 10);
			}
		}
	}
	return rs;
};

/*
 * App-specific functions/objects
 */
ARK.app.db = null;

ARK.app.openDatabase = function () {
	var testQueryTimer;

	function testDatabase (failure) {
		// Don't do the test for windows8
		if (ARK.isWindows8()) {
			loadData();
			return;
		}

		// Start a timeout. On Android, a deadlock happens sometimes when trying to get a connection
		testQueryTimer = setTimeout(function () {
			// Log error
			ARK.logError('Failed to run test query in <250ms.');
		}, 250);

		// Simple, quick query to test database access
		ARK.app.db.transaction(function (tx) {
			tx.executeSql('SELECT * FROM ProductOrderSpecColumnNames', [], loadData, failure);
		});
	}

	function loadData () {
		try {
			var lastUpdateTimestamp;

			clearTimeout(testQueryTimer);

			if (ARK.app.debug) { alert('database opened'); }
			ARK.app.initializationStatus.databaseOpened = true;
			ARK.app.initialized();

			lastUpdateTimestamp = window.localStorage.getItem('lastUpdateTimestamp-' + ARK.settings.language);

			ARK.app.getLocaleTimestamp(lastUpdateTimestamp || ARK.settings.appDatabaseTimestamp,
					function (timestamp) {
						ARK.settings.lastUpdateTimestamp = timestamp;
					}, function (error) {
						ARK.logError('getLocaleTimestamp failed. Error=' + error);
					});
		} catch (e) {
			ARK.onError(e.message, undefined, undefined, e.stack, 'loadData (openDatabase)');
		}
	}

	function testFailed (tx, err) {
		function overwriteDatabase () {
			if (ARK.app.debug) { alert('database closed'); }
			ARK.app.overwriteDatabase(function () {
				ARK.app.db = window.sqlitePlugin.openDatabase({name: 'ark', bgType: 1});
				if (ARK.app.debug) { alert('database opened'); }

				testDatabase(function (tx, e) {
					// Failed to load a second time. Log it, tell the user and stop.
					clearTimeout(testQueryTimer);
					ARK.app.logDatabaseError(e, 'testDatabase called from testFailed');
					ARK.alert(ARK.translations.get('DownloadFailed'), ARK.translations.get('OK'));
				});
			}, function (error) {
				ARK.alert(ARK.translations.get('DownloadFailed'), ARK.translations.get('OK'));
				ARK.logError('overwriteDatabase failed: ' + error);
			});
		}

		try {
			// Stop the first test timeout
			clearTimeout(testQueryTimer);

			ARK.app.logDatabaseError(err, 'testDatabase');

			ARK.app.db.close();
			// TODO: Fix close to call the callbacks
			// close doesn't call the callbacks on Android...
			// 100ms should be sufficient to close a database...
			setTimeout(overwriteDatabase, 100);
		} catch (e) {
			ARK.onError(e.message, undefined, undefined, e.stack, 'testFailed (openDatabase)');
		}
	}

	// Clear any cached data
	ARK.app.topLevel = null;
	ARK.app.secondLevel = null;
	ARK.app.currentCategory = null;
	ARK.app.currentProduct = null;
	ARK.app.currentExternalLinks = null;
	ARK.app.currentCodenameParentCategory = null;
	ARK.dbCache = {};
	ARK.searchLoaded = 0;
	ARK.codenamesSearchCache = [];
	ARK.productNamesSearchCache = [];
	ARK.orderCodeSearchCache = [];
	ARK.specCodeSearchCache = [];
	ARK.graphicsSearchCache = [];
	ARK.socketSearchCache = [];

	ARK.app.copyDatabase(ARK.settings.language, function () {
		ARK.app.db = window.sqlitePlugin.openDatabase({name: 'ark', bgType: 1});

		// Sometimes we're getting multiple kinds errors from SQLite after opening the database.
		// So, we do a sample query to see if the database is correct or not.
		// If that succeeds then it's a successful open and we'll load the rest of the data.

		testDatabase(testFailed);
	});
};

ARK.app.logDatabaseError = function (e, fromFunction, options) {
	options = options || {};
	ARK.logError('Database error in ' + fromFunction + '. code=' + e.code + ', message=' + e.message, options);
};

