ARK.app.loadBadges = function () {
	var db = ARK.app.db;

	db.transaction(function (tx) {
		tx.executeSql('select Value from Parameters where Name = \'UpdateBadge\'', [], function (tx, rs) {
			try {
				var updateBadgeDate,
					appLastBadgeDownloadDate = new Date(window.localStorage.getItem('lastBadgeDownloadDate'));

				if (rs.rows.length === 1) {
					updateBadgeDate = new Date(rs.rows.item(0).Value);
					if (updateBadgeDate.getTime() > appLastBadgeDownloadDate.getTime()) {
						db.transaction(function (tx) {
							tx.executeSql('SELECT DISTINCT ProcessorBrandId FROM Product WHERE HasBadge = 1', [], function (tx, rs) {
								try {
									var i, ids;

									ARK.dbState.loadBadges.inProgress = false;

									ids = '';

									for (i = 0; i < rs.rows.length; i++) {
										ids += rs.rows.item(i).ProcessorBrandId + ',';
									}
									ids = ids.slice(0, -1);
									ARK.app.downloadBadges(ids, function () {
										ARK.setLocalStorageItem('lastBadgeDownloadDate', ARK.app.getDate(new Date()));
									}, function (error) {
										ARK.logError('Error updating badges (downloadBadges). Error=' + error);
									});
								} catch (e) {
									ARK.dbState.loadBadges.inProgress = false;
									ARK.onError(e.message, undefined, undefined, e.stack, 'loadBadges (badge ids)');
								}
							}, function (tx, e) {
								ARK.dbState.loadBadges.inProgress = false;
								ARK.logError('Error updating badges (querying badge ids). code=' + e.code + ', message=' + e.message);
							});
						});
					} else {
						ARK.dbState.loadBadges.inProgress = false;
					}
				} else {
					ARK.dbState.loadBadges.inProgress = false;
				}
			} catch (e) {
				ARK.dbState.loadBadges.inProgress = false;
				ARK.onError(e.message, undefined, undefined, e.stack, 'loadBadges (parameters)');
			}
		}, function (tx, e) {
			ARK.dbState.loadBadges.inProgress = false;
			ARK.logError('Error updating badges (querying parameters). code=' + e.code + ', message=' + e.message);
		});
	});
	ARK.dbState.loadBadges.inProgress = true;
};

