ARK.logError = function (message, options) {
	var url, deviceHashValue, downloadDate, truncated, originalLength;

	options = options || { severity: ARK.ERROR_ID };

	if (ARK.hasConnection()) {
		try {
			url = ARK.settings.logErrorServer;
			deviceHashValue = '';
			downloadDate = '';
			truncated = 0;
			originalLength = message.length;

			try {
				deviceHashValue = window.localStorage.getItem('hash-' + ARK.settings.language);
				downloadDate = window.localStorage.getItem('lastUpdateTimestamp-' + ARK.settings.language);
			}
			catch (err) {
				// Ignore any errors trying to get data
			}

			options.severity = options.severity || ARK.ERROR_ID;

			// Don't send super huge errors
			if (message.length > 144434) {
				truncated = 1;
				if (message.indexOf('\\"data_bin\\":') > -1) {
					message = message.substring(0, 1024);
				} else {
					message = message.substring(0, 144434);
				}
			}

			// Send the data using post
			$.post(url,
				{	HandlePost: '',
					truncated: truncated,
					originalLength: originalLength,
					doubleEncoded: 1,
					// Double encode because IIS doesn't like HTML in values
					errorMessage: encodeURIComponent(message),
					page: options.currentPage || ARK.history.currentPage,
					pageBeingBuilt: ARK.history.pageBeingBuilt,
					parameters: options.parameters,
					devicePlatform: ARK.devicePlatform(),
					deviceType: ARK.deviceType,
					osVersion: ARK.deviceVersion(),
					userAgent: navigator.userAgent,
					appVersion: ARK.version,
					databaseHash: deviceHashValue,
					downloadDate: downloadDate,
					deviceName: typeof device === 'undefined' ? 'unknown: device object not yet created' : device.model,
					messageType: options.severity,
					data: options.data,
					language: ARK.settings.language,
					connectionType: ARK.connectionType,
					stack: options.stack !== undefined ? encodeURIComponent(options.stack) : undefined
				}
			);
		}
		catch (err) {
			return false;
		}
	} else {
		// TODO: Determine what action to take if no connection to post error
	}
};

