ARK.app.getDatabase = function (data) {
	var jqData;

	if (ARK.settings.useProductionOdata) {
		return data.d;
	} else {
		jqData = $(data);
		return {
			language: jqData.find('language').text(),
			checksum: jqData.find('checksum').text(),
			data_bin: jqData.find('data_bin').text()
		};
	}
};

ARK.app.getDiff = function (data) {
	var jqData, diff;

	if (ARK.settings.useProductionOdata) {
		return data.d;
	} else {
		jqData = $(data);
		diff = $(data).find('data_diff');
		if (diff.length === 0) {
			return undefined;
		} else {
			return {
				data_diff: jqData.find('data_diff').text(),
				clientChecksum: jqData.find('clientChecksum').text(),
				endingChecksum: jqData.find('endingChecksum').text()
			};
		}
	}
};
